mapper.select:
SELECT no,subject,name,DATE_FORMAT(regdate,'%Y-%m-%d') as dbday,hit
FROM board ORDER BY no DESC
LIMIT #{start},#{end}

mapper.update:
UPDATE board SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,name,subject,content,DATE_FORMAT(regdate,'%Y-%m-%d') as dbday,hit
FROM board
WHERE no=#{no}
